function logout(server){
	$.ajax({
		url: 'http://forum'+server+'.hkgolden.com/logout.aspx',
		success: function(response){
			$("#username").html('CD-ROM');
			$("#login").html('Login').attr('data-action','login');
			noty({
				text: 'Logout success on server '+server,
				layout: 'bottomRight',
				type: 'success',
				timeout: 3000
			});
		}
	});
}